StructuredTextModel.Text:
IF in_AdditivePump AND sAdditiveControl_Input.AdditivePumpStart AND sAdditiveControl_Input.AdditivePumpForward AND NOT sAdditiveControl_Input.OnExtrude THEN
			L_ForwardSignal := L_out_ForwardSignal;
			L_out_ReverseSignal := FALSE;
			L_out_PrintheadValve := TRUE;
		ELSIF in_AdditivePump AND sAdditiveControl_Input.AdditivePumpStart AND sAdditiveControl_Input.AdditivePumpForward AND sAdditiveControl_Input.OnExtrude THEN
			IF L_in_ExtruderInStandstill = FALSE THEN
				L_ForwardSignal := L_out_ForwardSignal;
				L_out_ReverseSignal := FALSE;
				L_out_PrintheadValve := TRUE;
			ELSE
				L_ForwardSignal := FALSE;
				L_out_PrintheadValve := FALSE;
			END_IF;
		ELSIF in_AdditivePump AND sAdditiveControl_Input.AdditivePumpStart AND sAdditiveControl_Input.AdditivePumpReverse THEN
			L_ReverseSignal := L_out_ReverseSignal;
			L_out_ForwardSignal := FALSE;	
		ELSIF sAdditiveControl_Input.AdditivePumpStart AND sAdditiveControl_Input.Fill THEN
			L_ForwardSignal := TRUE;
			L_ReverseSignal := FALSE;
			sAdditiveControl_Input.Empty := FALSE;
			L_out_PrintheadValve := TRUE;
		ELSIF sAdditiveControl_Input.AdditivePumpStart AND sAdditiveControl_Input.Empty THEN
			L_ForwardSignal := FALSE;
			L_ReverseSignal := TRUE;
			sAdditiveControl_Input.Fill := FALSE;
			L_out_PrintheadValve := TRUE;
		ELSE
			L_ForwardSignal := FALSE;
			L_ReverseSignal := FALSE;
			L_out_PrintheadValve := FALSE;
		END_IF;
