StructuredTextModel.Text:
(* FLOW SENSOR - Convert 4-20mA to L/min *)
IF ai_Flow_4to20mA >= 6553 THEN
    rFlowRate_LPM := ((INT_TO_REAL(ai_Flow_4to20mA) - 6553.0) / 26214.0) * 1.45 + 0.05;
    
    IF rFlowRate_LPM < 0.05 THEN
        rFlowRate_LPM := 0.0;
    END_IF;
    
    IF rFlowRate_LPM > 1.5 THEN
        rFlowRate_LPM := 1.5;
    END_IF;
ELSE
    rFlowRate_LPM := 0.0;
END_IF;

(* PRESSURE SENSOR - Convert bytes to bar *)
wPressure := SHL(BYTE_TO_WORD(awPressure_Data[0]), 8) OR BYTE_TO_WORD(awPressure_Data[1]);
rPressure_Bar := (WORD_TO_REAL(wPressure) / 65535.0) * 10.0;

IF rPressure_Bar < 0.0 THEN
    rPressure_Bar := 0.0;
END_IF;

IF rPressure_Bar > 10.0 THEN
    rPressure_Bar := 10.0;
END_IF;
