StructuredTextModel.Text:
(* Read the raw data bytes *)
bLow := IFM_P1_DATAI[0];
bHigh := IFM_P1_DATAI[1];

(* Combine into WORD *)
wPressure := BYTE_TO_WORD(bLow) OR SHL(BYTE_TO_WORD(bHigh), 8);

(* Convert to signed INT *)
iPressure_Raw := WORD_TO_INT(wPressure);

(* Convert to millibar *)
iPressure_mBar := (iPressure_Raw * 10000) / 32767;
