StructuredTextModel.Text:
(* Read pressure from ILM400 *)
wPressure := SHL(BYTE_TO_WORD(IFM_P1_DATAI[0]), 8) 
             OR BYTE_TO_WORD(IFM_P1_DATAI[1]);

(* Convert: 0-65535 = -1 to 10 bar *)
rPressure_Bar := (WORD_TO_REAL(wPressure) / 65535.0) * 11.0 - 1.0;

(* Bounds *)
IF rPressure_Bar < -1.0 THEN rPressure_Bar := -1.0; END_IF;
IF rPressure_Bar > 10.0 THEN rPressure_Bar := 10.0; END_IF;
